"SUCCESS" | 
/**
Magnetic ink strip was found, but validation failed, because of a format violation, an unsupported format, or an OCR error.
*/
"INCOMPLETE_VALIDATION" | 
/**
Magnetic ink strip was not found.
*/
"ERROR_NOTHING_FOUND";
